package com.example.navdrawerexample.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.navdrawerexample.Item;
import com.example.navdrawerexample.MyAdapter;
import com.example.navdrawerexample.R;

import java.util.ArrayList;
import java.util.List;

public class FavoritDrink extends Fragment {
    List<Item> drink = new ArrayList<>();
    MyAdapter adapter;

    String[] image ={
            "https://gratisongkir-storage.com/products/900x900/w3voqr751GBY.jpg",
            "https://images.tokopedia.net/img/cache/700/VqbcmM/2021/3/6/c2c06fea-bc00-4621-b3e2-0c86d4be27ab.jpg",
            "https://www.masakapahariini.com/wp-content/uploads/2020/08/Resep-Bajigur-Milk-Tea.jpg",
            "https://www.tangerangkota.go.id/files/berita/34037es-teler-segar-untuk-takjil-buka-puasa-intip-resepnya-34037.jpeg",
            "https://cdn0-production-images-kly.akamaized.net/WVcOmtrtq0-SE41cZNXrM7BRXiA=/1200x1200/smart/filters:quality(75):strip_icc():format(webp)/kly-media-production/medias/1560632/original/067454600_1491639480-Susu-Putih3.jpg",
            "https://cdn1-production-images-kly.akamaized.net/4V1qrd9nzlX00rCRYxsm_twFj7E=/800x450/smart/filters:quality(75):strip_icc():format(webp)/kly-media-production/medias/1921340/original/088464600_1519205225-iStock-865227762.jpg",
            "https://awsimages.detik.net.id/community/media/visual/2022/01/02/manfaat-kopi_169.jpeg?w=600&q=90",
            "data:image/jpeg;base64,",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTWXWVuZS0yqBupCQFMBdvj6UVaD4-8av2mreODJ6Qc_w&s"};

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        View rootView = inflater.inflate(R.layout.fragment_drink, container, false);
        return rootView;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        RecyclerView recyclerView = view.findViewById(R.id.RvDrink);
        // Set the title of the Fragment
        requireActivity().setTitle("My favorit food");

        menu();

        adapter = new MyAdapter(getContext(), drink);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
    }

    private void menu() {
        String[] title =getResources().getStringArray(R.array.minumanTitle);
        String[] desc =getResources().getStringArray(R.array.minumanDesc);

        for (int i=0; i<title.length;i++){
            drink.add(new Item(image[i],title[i],desc[i]));
        }

    }
}

