package com.example.navdrawerexample.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.navdrawerexample.Item;
import com.example.navdrawerexample.MyAdapter;
import com.example.navdrawerexample.R;

import java.util.ArrayList;
import java.util.List;

public class FavoritFood extends Fragment {

    List<Item> food = new ArrayList<>();
    MyAdapter adapter;

    String[] image ={
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "https://cdn1.katadata.co.id/media/images/thumb/2022/08/30/Cilor-2022_08_30-12_02_57_d7e6737cd0df7c233e88a7628bfa888b_960x640_thumb.jpg",
            "https://www.static-src.com/wcsstore/Indraprastha/images/catalog/full//93/MTA-2583228/indomie_indomie-goreng-mie-instan--85g--_full02.jpg",
            "https://static.promediateknologi.id/crop/0x0:0x0/750x500/webp/photo/2023/01/18/1406417853.jpg",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTKcTCvQjRSFmqlGFM5-NQAfJb1fnXK_HdS0EcTA0eVvA&s",
            "https://cdn.antarafoto.com/cache/1200x800/2017/03/04/makan-gratis-bagi-dhuafa-eioc-dom.webp"};

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        View rootView = inflater.inflate(R.layout.fragment_food, container, false);
        return rootView;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        RecyclerView recyclerView = view.findViewById(R.id.RvFood);
        // Set the title of the Fragment
        requireActivity().setTitle("My favorit food");

        menu();

        adapter = new MyAdapter(getContext(),food);
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
    }

    private void menu() {
        String[] title =getResources().getStringArray(R.array.makananTitle);
        String[] desc =getResources().getStringArray(R.array.makananDesc);

        for (int i=0; i<title.length;i++){
            food.add(new Item(image[i],title[i],desc[i]));
        }

    }
}
